<?php
$Base64Img = 'data:image/jpeg;base64,data:image/png;base64,';
$Url = 'ua/upload';
$Name = rand(10,1000000);
$Base64Img = str_replace('data:image/jpeg;base64,data:image/png;base64,','',$Base64Img);
$bitmap = base64_decode($Base64Img);
$file = $Url.'/'.$Name.'.jpg';
file_put_contents($file,$bitmap);